mapper.select:
select * from (select rownum as rnum, c.* from ( select camp.camp_no as campNo, camp_name as campName, camp_addr as campAddr, camp_site as campSite, camp_tel as campTel, camp_theme as campTheme, camp_fac as campFac, camp_ctg as campCtg, campenv.env_name as campEnv, (select min(room_price) from camproom where camp_no = camp.camp_no) as campMinPrice, (select round(avg(rev_star),1) from review where camp_no = camp.camp_no) as campStar, (select camp_filepath from file_tbl where file_no = (select min(file_no) from file_tbl where camp_no = camp.camp_no)) as imgpath from camp full join campenv on (camp.camp_no = campenv.camp_no) where
camp_name like '%'||#{keyword}||'%'
camp_addr like '%'||#{keyword}||'%'
)c) where rnum between ${start} and ${end}
